sys_script.script:
(function executeRule(current, previous) {

	var currentAssignee = current.assigned_to.getRefRecord();
	var previousAssignee = previous.assigned_to.getRefRecord();
	var listAssignees = [currentAssignee, previousAssignee];
	var isAssigneeEmpty = (gs.nil(current.assigned_to)) ? true : false;

	var updateUserRecord = function (user) {
		// This function will just populate the payload field on the user table
		// Business Rule running on the user table will pick it up and parse it
		try {
			var Counter = new TaskCounter({userIdentifier: user});
			user.setValue(
				"x_441902_task_ac_raw_assignment_payload", 
				Counter.getTotalByAssignee()
			);
			user.update();
		} catch (error) {
			gs.error(error);
		}
	};

	if (current.operation() === "insert") {
		// For new records we only process if the assgned_to field is not empty
		// Some clients make the assigned_to always mandatory but we don't take any risks
		if (isAssigneeEmpty) {
			return;
		}
		updateUserRecord(currentAssignee);
		return;
	}

	if (current.operation() === "update") {
		if (current.assigned_to.changes()) {
			if (isAssigneeEmpty) {
				// If the assigned_to field can be changed to empty
				// we need to make sure we process using the previous
				updateUserRecord(previousAssignee);
				return;
			}
			// We now know that we need to process for current and previous assignees
			listAssignees.forEach(function (user) {
				updateUserRecord(user);
			});
		}
	}

})(current, previous);
