sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    var ASSIGNED_TO_FIELD = control.name.split(".")[1];

    g_form.hideFieldMsg(ASSIGNED_TO_FIELD, true);

    if (!g_scratchpad.canRun || isLoading || newValue === "") {
        return;
    }

    var assignedTo = g_form.getReference(ASSIGNED_TO_FIELD, function parse(output) {
        var total = 0;
        var messageType = "error";
        var mapping = g_scratchpad.MAPPING;
        var threshold = g_scratchpad.THRESHOLDS;
        if (output) {
            for (var table in mapping) {
                if (table === g_form.getTableName()) {
                    total = output[mapping[table]];
                    if (total >= 0 && total <= threshold.LOW) {
                        messageType = "info";
                    } else if (total > threshold.LOW && total <= threshold.MEDIUM) {
                        messageType = "warning";
                    }
                    g_form.showFieldMsg(
                        ASSIGNED_TO_FIELD,
                        "This user has " + total + " " + table + "(s) assigned",
                        messageType
                    );
                    break;
                }
            }
        }
    });
}
