sys_script.script:
(function executeRule(current, previous) {

    var rawPayload = current.getValue(TaskCounter.FIELDS.RAW_PAYLOAD);
    var mapping = JSON.parse(gs.getProperty(TaskCounter.PROPS.PAYLOAD_MAP));
    var isEmpty = (gs.nil(rawPayload)) ? true : false;
    var parsedPayload = {};
    var jsonSummary = "";

	if (isEmpty) {
		for (var field in mapping) {
			if (!current.isValidField(mapping[field])) {
				continue;
			}
			current.setValue(mapping[field], 0);
		}
		return;
	}

    parsedPayload = JSON.parse(rawPayload);
    jsonSummary = JSON.stringify(parsedPayload[current.getUniqueValue()]);

    if (!gs.nil(jsonSummary)) {
        current.setValue(TaskCounter.FIELDS.SUMMARY, jsonSummary);
        for (var key in mapping) {
            if (!current.isValidField(mapping[key])) {
                continue;
            }
            current.setValue(
                mapping[key],
                parseInt(parsedPayload[current.getUniqueValue()][key])
            );
        }
    }

})(current, previous);
